scene.onOverlapTile(SpriteKind.Worldcutting, sprites.castle.tileGrass2, function (sprite, location) {
    tiles.setTileAt(location, assets.tile`myTile1`)
    if (Math.percentChance(1)) {
        tiles.setTileAt(location, assets.tile`myTile`)
    }
    if (Math.percentChance(0.5)) {
        tiles.setTileAt(location, assets.tile`transparency16`)
    }
    if (Math.percentChance(0.1)) {
        tiles.setTileAt(location, sprites.dungeon.hazardWater)
    }
})
statusbars.onStatusReached(StatusBarKind.Power, statusbars.StatusComparison.EQ, statusbars.ComparisonType.Fixed, 8, function (status) {
    status.setLabel("8")
})
statusbars.onStatusReached(StatusBarKind.Power, statusbars.StatusComparison.EQ, statusbars.ComparisonType.Fixed, 10, function (status) {
    status.setLabel("10")
})
sprites.onOverlap(SpriteKind.KingofCurses, SpriteKind.CursedObject, function (sprite, otherSprite) {
    sprites.destroy(otherSprite)
    Sukunas_Power.value += 1
})
